root.content:
&lt;p&gt;
	&lt;strong&gt;&lt;span&nbsp;style=\"font-size:14px;\"&gt;jQuery&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span&nbsp;style=\"font-size:14px;\"&gt;+CSS&lt;/span&gt;&lt;/strong&gt;编写的&lt;strong&gt;&lt;span&nbsp;style=\"font-size:14px;\"&gt;下拉菜&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span&nbsp;style=\"font-size:14px;\"&gt;单&lt;/span&gt;&lt;/strong&gt;，有简单带&lt;strong&gt;&lt;span&nbsp;style=\"font-size:14px;\"&gt;淡出动画效果&lt;/span&gt;&lt;/strong&gt;，这个实例没有编写太复杂的CSS样式，比较简单，喜欢大家喜欢哦。
&lt;/p&gt;
&lt;p&gt;
&lt;pre&nbsp;name=\"code\"&nbsp;id=\"prettyprint\"&nbsp;class=\"css\"&gt;&nbsp;&amp;lt;!DOCTYPE&nbsp;html&nbsp;PUBLIC&nbsp;\"-//W3C//DTD&nbsp;XHTML&nbsp;1.0&nbsp;Transitional//EN\"&nbsp;\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\"&amp;gt;
&amp;lt;html&nbsp;xmlns=\"http://www.w3.org/1999/xhtml\"&amp;gt;
&amp;lt;head&amp;gt;
&amp;lt;meta&nbsp;http-equiv=\"Content-Type\"&nbsp;content=\"text/html;charset=GBK\"&nbsp;/&amp;gt;
&amp;lt;title&amp;gt;jQuery下拉菜单&nbsp;-&nbsp;Liehuo.Net&amp;lt;/title&amp;gt;
&amp;lt;script&nbsp;type=\"text/javascript\"&nbsp;src=\"/uploads/common/js/jquery-1.3.2.js\"&amp;gt;&amp;lt;/script&amp;gt;
&amp;lt;style&amp;gt;
.test{&nbsp;margin:10px;}
.topMenu{&nbsp;position:relative;}
.topMenu&nbsp;span{&nbsp;position:absolute;&nbsp;top:15px;&nbsp;border:#ccc&nbsp;1px&nbsp;solid;&nbsp;padding:5px;&nbsp;width:60px;}
&amp;lt;/style&amp;gt;
&amp;lt;script&nbsp;type=\"text/javascript\"&amp;gt;
$(function(){
//$(\".test\").append(\"&amp;lt;br/&amp;gt;在.test里面后置字符串\");
//$(\"b\").appendTo(\"p\");&nbsp;//把b后置到p
//$(\".test\").prepend(\"在.test里面前置字符串\");
//$(\"p\").prepend($(\"b\"));&nbsp;//把b前置到.test
//$(\"b\").prependTo(\"p\");&nbsp;//把b前置到.test
$(\".topMenu&nbsp;a\").click(function(){
if($(this).siblings().length==0){
$(this).parent().append(\"&amp;lt;span&amp;gt;栏目001&amp;lt;br/&amp;gt;栏目002&amp;lt;br/&amp;gt;栏目003&amp;lt;br/&amp;gt;栏目004&amp;lt;/span&amp;gt;\");
}
else{
$(this).siblings().show();
}
}).parent().hover(function(){},function(){
$(this).find(\"span\").fadeOut();
})
})
$(window).resize(function(){
alert(\"Stop&nbsp;it!\");
});
&amp;lt;/script&amp;gt;
