	&lt;/p&gt;
&lt;font&nbsp;color=\"#222222\"&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&gt;&lt;font&nbsp;color=\"#222222\"&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&nbsp;font-family:=\"\"&nbsp;tahoma,=\"\"&nbsp;helvetica,=\"\"&nbsp;simsun,=\"\"&nbsp;sans-serif;=\"\"&nbsp;line-height:=\"\"&nbsp;21px;=\"\"&nbsp;text-align:=\"\"&nbsp;-webkit-auto;=\"\"&gt;&lt;ignore_js_op&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&gt;&lt;img&nbsp;id=\"aimg_1260\"&nbsp;aid=\"1260\"&nbsp;src=\"http://bbs.phphubei.com/data/attachment/forum/201205/15/112519gs5gz3u3sfb1f1gc.jpg\"&nbsp;zoomfile=\"data/attachment/forum/201205/15/112519gs5gz3u3sfb1f1gc.jpg\"&nbsp;file=\"data/attachment/forum/201205/15/112519gs5gz3u3sfb1f1gc.jpg\"&nbsp;class=\"zoom\"&nbsp;width=\"531\"&nbsp;inpost=\"1\"&nbsp;alt=\"1_110726095132_1.jpg\"&nbsp;title=\"1_110726095132_1.jpg\"&nbsp;lazyloaded=\"true\"&nbsp;initialized=\"true\"&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&nbsp;cursor:=\"\"&nbsp;pointer;=\"\"&gt;&nbsp;&lt;/ignore_js_op&gt;&lt;br&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&gt;&lt;br&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&gt;&lt;strong&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&nbsp;font-weight:=\"\"&nbsp;700;=\"\"&gt;&lt;font&nbsp;color=\"#ff00\"&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&gt;这个表单使用哪些新元素类型及功能？&lt;br&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&gt;&lt;br&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&gt;&lt;/span&gt;&lt;/strong&gt;&lt;font&nbsp;color=\"#ff00\"&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&gt;&lt;br&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&nbsp;color:=\"\"&nbsp;rgb(68,=\"\"&nbsp;68,=\"\"&nbsp;68);=\"\"&nbsp;font-family:=\"\"&nbsp;tahoma,=\"\"&nbsp;helvetica,=\"\"&nbsp;simsun,=\"\"&nbsp;sans-serif;=\"\"&nbsp;line-height:=\"\"&nbsp;21px;=\"\"&nbsp;text-align:=\"\"&nbsp;-webkit-auto;=\"\"&gt;&lt;font&nbsp;color=\"#222222\"&nbsp;style=\"word-wrap:\"&nbsp;break-word;=\"\"&nbsp;font-family:=\"\"&nbsp;tahoma,=\"\"&nbsp;helvetica,=\"\"&nb